#coding:utf-8import csvimport jsonimport requestsfrom bs4 import BeautifulSoupfrom collections import namedtupleheaders = {'User-Agent':'Mozilla/5.0 (Macintosh; Intel Mac OS X 10_12_3) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/60.0.3112.113 Safari/537.36'}r = requests.get('http://seputu.com/',headers=headers)#print(r.text)content = []soup = BeautifulSoup(r.text,'html.parser',from_encoding='uft-8')for mulu in soup.find_all(class_="mulu"):    h2 = mulu.find('h2')    if h2 != None:        h2_title = h2.string #获取标题        list=[]        for a in mulu.find(class_='box').find_all('a'):            href = a.get('href')            box_title = a.get('title')            list.append({'href':href,'box_title':box_title})        content.append({'title':h2_title,'content':list})with open('qiye.json','wb') as fp:    json.dump(content,fp=fp,indent=4)